import React, { Component } from 'react'

import { connect } from 'react-redux'

import { getItemsId } from '../../../../redux/actions/productActions'

import PropTypes from 'prop-types'

export class ViewSpecs extends Component {

    componentDidMount() {
        this.props.getItemsId(this.props.match.params.id)
    }

    render() {
        const { products } = this.props
        console.log(products)
        const productsList = products.length ? (products.map(product => {
            return(
                <div>
                    <h1>{product.brand}</h1>
                    <li>Cores: {product.cores}</li>
                    <li>Threads: {product.threads}</li>
                    <li>Base Frequency: {product.base_frequency}</li>
                    <li>Turbo Frequency(Max): {product.turbon_frequency}</li>
                    <li>Cache: {product.cache}</li>
                    <li>Memory Type: {product.memory_type}</li>
                </div>
            )
        })) : null

        return (
            <div>
                {productsList}
            </div>
        )
    }
}

ViewSpecs.propTypes = {
    getItems: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => {
    return {
        products: state.products.products
    }
}

export default connect(mapStateToProps, { getItemsId })(ViewSpecs)
