import React, { Component } from 'react'

import { connect } from 'react-redux'

import { getItems } from '../../../../redux/actions/productActions'

import PropTypes from 'prop-types'

export class ViewSpecs extends Component {

    componentDidMount() {
        this.props.getItems()
    }

    render() {
        const { products, match } = this.props

        const getID = products.map(product => {
            if(product._id === match.params.id){
                return(
                    <h1> Hello </h1>
                )
            }
        })

        if(getID === match.params.id){
            return(
                <h1> WOW </h1>
            )
        }
        
        return (
            <div>
                <h1>{getID}</h1>
            </div>
        )
    }
}

ViewSpecs.propTypes = {
    getItems: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => {
    return {
        products: state.products.products
    }
}

export default connect(mapStateToProps, { getItems })(ViewSpecs)
