import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Button } from 'reactstrap'
import { getItemsID } from '../../../../redux/actions/productActions'

export class ViewSpecs extends Component {

    componentDidMount(){
        this.props.getItemsID()
    }

    buttonHandler = () => {
        console.log('click')
    }

    render() {
        return (
            <Button
            color='dark'
            style={{marginBottom: '1rem'}}
            onClick={this.buttonHandler}
            >View Full Specification</Button>
        )
    }
}

const mapStateToProps = (state) => ({
        ViewSpecs: state.products.products
})


export default connect(mapStateToProps, { getItemsID })(ViewSpecs)
