import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Button } from 'reactstrap'
import { getItemsID } from '../../../../redux/actions/productActions'

export class ViewSpecs extends Component {

    componentDidMount(){
        this.props.getItemsID()
    }

    render() {
        return (
            <Button
            color='dark'
            style={{marginBottom: '1rem'}}
            >View Full Specification</Button>
        )
    }
}

const mapStateToProps = (state) => ({
        products: state.products.products
})

const mapDispatchToProps = {
    
}

export default connect(mapStateToProps, { getItemsID },mapDispatchToProps)(ViewSpecs)
