import React, { Component } from 'react'

import { connect } from 'react-redux'

import { getItems } from '../../../../redux/actions/productActions'

import PropTypes from 'prop-types'

export class ViewSpecs extends Component {

    componentDidMount() {
        this.props.getItemsId(this.props.match.params.id)
    }

    render() {
        const { products, match } = this.props

        const getID = products.map(product => {
                return product._id === match.params.id
        })

        console.log(getID)

        const specs = getID.lenght ? (products.map(product => {
            return(<h1>Tow</h1>)
        })) : null

        return (
            <div>
                <h1>{specs}</h1>
            </div>
        )
    }
}

ViewSpecs.propTypes = {
    getItems: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => {
    return {
        products: state.products.products
    }
}

export default connect(mapStateToProps, { getItems })(ViewSpecs)
