import React, { Component } from 'react'

import { connect } from 'react-redux'

import { getItems } from '../../../../redux/actions/productActions'

import PropTypes from 'prop-types'

export class ViewSpecs extends Component {

    componentDidMount() {
        this.props.getItems()
    }

    render() {
        const { products, match } = this.props
        const viewSpec = products.map(product => {
            return product._id
        })
        if(viewSpec === match.params.id){
            return(
                <h1> WOW </h1>
            )
        }
        
        return (
            <div>
                <h1>{match.params.id}</h1>
            </div>
        )
    }
}

ViewSpecs.propTypes = {
    getItems: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => {
    return {
        products: state.products.products
    }
}

export default connect(mapStateToProps, { getItems })(ViewSpecs)
